import axios from"axios";export async function resetDatabase(){let message="",messageClass="";try{message=(await axios.post("http://localhost:3000/api/reset")).data.message,messageClass=message.includes("succès")?"success":"error"}catch(error){console.error(error),message="Erreur lors de la réinitialisation de la base de données",messageClass="error"}return{message:message,messageClass:messageClass}}